import {Fragment, useEffect, useState} from "react"
import {useSelector, useDispatch} from "react-redux"
import {useParams} from "react-router-dom"
import {getEntries} from "../store/reducers/action"
const UpdateEntry = () => {
    const dispatch = useDispatch()
    const entries = useSelector(state => state.entries)
    useEffect(() => {
        console.log(" in here ====")
        if(!entries){
            dispatch(getEntries())
        }

      }, [dispatch, entries]) 
    const params = useParams()
    const {id} = params
    const entry = useSelector(state => state.entries.find(data => data._id === id))
    console.log(entry)
     return (
        <Fragment>
            <label>Title</label>
            <p contentEditable="true">{entry.title}</p>
            <label>Topic</label>
            <p contentEditable="true">{entry.topic}</p>
            <label>Entry</label>
            <p contentEditable="true">{entry.entry}</p>
            <button className="btn">Save</button>
            <button className="btn">Cancel</button>
        </Fragment>
    ) 
}
export default UpdateEntry
