import useInput from "../hooks/use-input"
import useHttp from "../hooks/use-http"
import {useEffect, useState} from "react"
const Signup = () => {
    const checkNull = val => val.trim() !== ""
    const checkMatch = val => val.trim() !== passwordValue.trim()
    const {
        value: firstNameValue, 
        hasError: firstNameError,
        inputValueChangeHandler: firstNameChangeHandler,
        inputValueBlurHandler: firstNameBlurHandler,
        isValid: firstNameIsValid
    } 
    = useInput(checkNull)

    const {
        value: lastNameValue, 
        hasError: lastNameError,
        inputValueChangeHandler: lastNameChangeHandler,
        inputValueBlurHandler: lastNameBlurHandler,
        isValid: lastNameIsValid
    } 
    = useInput(checkNull)

    const {
        value: emailValue, 
        hasError: emailError,
        inputValueChangeHandler: emailChangeHandler,
        inputValueBlurHandler: emailBlurHandler,
        isValid: emailIsValid
    } 
    = useInput(checkNull)
    const {
        value: passwordValue, 
        hasError: passwordError,
        inputValueChangeHandler: passwordChangeHandler,
        inputValueBlurHandler: passwordBlurHandler,
        isValid: passwordIsValid
    } 
    = useInput(checkNull)
    const {
        value: rePasswordValue, 
        hasError: rePasswordError,
        inputValueChangeHandler: rePasswordChangeHandler,
        inputValueBlurHandler: rePasswordBlurHandler,
        isValid: rePasswordIsValid
    } 
    = useInput(checkMatch)
    const {isLoading: loading, isError: errorMsg, sendRequest: sendData} = useHttp()
    const [emailCheckValue, setEmailCheck] = useState(null)

    useEffect(() => {
        const timer = setTimeout(() => {
            sendData({url: `http://localhost:8080/api/auth/checkEmail/${emailValue}`}, (data) => {
               console.log(data)
            })
        }, 5000)
        return () => {
            clearTimeout(timer)
            setEmailCheck(false)
        }
    }, [emailValue, emailUsed, setEmailCheck])

    const submitHandler = (e) => {
        e.preventDefault();
        console.log(firstNameValue)
    }


    return (
        <form className="input-form form-flex" onSubmit={submitHandler}>
            <label>First Name</label>
            <input type="text" value={firstNameValue} onChange={firstNameChangeHandler} onBlur={firstNameBlurHandler} className="input-field-text"/>
            {firstNameError && <p>First name is incorrect</p>}
            <label>Last Name:</label>
            <input type="text" value={lastNameValue} onChange={lastNameChangeHandler} onBlur={lastNameBlurHandler} className="input-field-text"/>
            {lastNameError && <p>Last name is incorrect</p>}
            <label>Email:</label>
            <input className="input-field-text" type="email" value={emailValue} onChange={emailChangeHandler} onBlur={emailBlurHandler}/>
            {emailError && <p>Email is not vaild</p>}
            {!emailError && emailCheckValue && <p>Email has already been used </p>}
            {!emailError && !emailCheckValue && <p>Email has not been used </p>}
            <label>Password</label>
            <input className="input-field-text"  type="password" value={passwordValue} onChange={passwordChangeHandler} onBlur={passwordBlurHandler}/>
            {passwordError && <p>Password is not vaild</p>}
            <label>Re enter password</label>
            <input className="input-field-text"  type="password" value={rePasswordValue} onChange={rePasswordChangeHandler} onBlur={rePasswordBlurHandler}/>
            {rePasswordError && <p>Password does not match</p>}
            <button className="btn">Sign Up</button>
        </form>
    )
}
export default Signup
