import {useDispatch} from "react-redux"
import {deleteEntry} from "../../store/reducers/action"
import {useNavigate} from "react-router-dom"
const EntryItem = (props) => {
    const dispatch = useDispatch()
    const navigate = useNavigate()
    const deleteHandler = (id) => {
        dispatch(deleteEntry(id))
    }
    const updateHandler = () => {
        navigate(`/entry/${props.id}`)
    }
    return (
        <section className="entry-item">
            <div onClick={() => deleteHandler(props.id)} className="btn-delete">X</div>
            <h3> Title: {props.title}</h3>
            <h3>Topic: {props.topic}</h3>
            <button onClick={updateHandler} className="btn">Update Entry</button>
        </section>
    )
}
export default EntryItem
