const initialState = {
    token: "",
    isLoggedIn: false,
    user: {}
}
const authReducer = (state = initialState, action) => {
    switch(action.type) {
        case "LOGIN":
            return {isLoggedIn: true , token: action.payload}
        case "LOGOUT":
            return {isLoggedIn: false, token: ""}
        default:
            return state
    }
}
export default authReducer
