import entries from "./entries"
import {combineReducers} from "redux"
export default combineReducers({
    entries: (state = [], action) => {
        switch(action.type) {
            case "ADD_ENTRY":
               return  state.concat(action.payload)

            case "Load_ENTRIES":
                return action.payload
            default:
                return state

        }   
    }
})
