    const params = useParams()
    const {id} = params
    const [entryValue, setEntry] = useState(null)
   useEffect(() => {
    const entry = entries.find(data => data._id == id)
    setEntry(entry)
    console.log(entry)
   }, [])

    return (
        <Fragment>
            <label>Title</label>
