const reducer = (state = [], action) => { 
    switch(action.type) {
        case "ADD_ENTRY":
           return  [...state, action.payload]

        case "LOAD_ITEMS":
            console.log("in load items")
            return action.payload
        case "DELETE_ENTRY":
            return state.filter(entry => entry._id !== action.payload)
        default:
            return state

    }   
}
export default reducer
