import useInput from "../hooks/use-input"
import useHttp from "../hooks/use-http"
const DiaryInput = () => {
    const checkNull = val => val.trim() !== ""
    const {
        value: titleValue, 
        hasError: titleError,
        inputValueChangeHandler: titleChangeHandler,
        inputValueBlurHandler: titleBlurHandler,
        resetHandler: titleReset,
        isValid: titleIsValid
    } 
    = useInput(checkNull)

    const {
        value: topicValue, 
        hasError: topicError,
        inputValueChangeHandler: topicChangeHandler,
        inputValueBlurHandler: topicBlurHandler,
        resetHandler: topicReset,
        isValid: topicIsValid
    } 
    = useInput(checkNull)

    const {
        value: enteryValue, 
        hasError: enteryError,
        inputValueChangeHandler: enteryChangeHandler,
        inputValueBlurHandler: enteryBlurHandler,
        resetHandler: enteryReset,
        isValid: enteryIsValid
    } 
    = useInput(checkNull)
    let formValid = false
    if(titleIsValid && topicIsValid && enteryIsValid) {
        formValid = true
    }

    const {isLoading: loading, isError: errorMsg, sendRequest: submitData} = useHttp()

    const handleData = (data) => {
        console.log(data);
    }

    const submitHandler = (e) => {
        e.preventDefault();
        console.log(titleValue, topicValue, enteryValue)
        submitData({
            url: "http://localhost:8080/api/entery/create",
            method: "POST",
            headers: {
                "Content-Type": "application json"
            },
            body: {
                title: titleValue,
                topic: topicValue,
                entery: enteryValue
            }
    }, handleData)
    }


    return (
        <form className="input-form form-flex" onSubmit={submitHandler}>
            <div className="input-field input-flex">
                <label>Title</label>
                <input className="input-field-text" onChange={titleChangeHandler} onBlur={titleBlurHandler} value={titleValue} type="text" name="title" placeholder="Title of entery"/>
                {titleError && <p className="input-error-text">Title is a required field</p>}
            </div>
            <div className="input-field input-flex">
                <label>Topic</label>
                <input onChange={topicChangeHandler} onBlur={topicBlurHandler} value={topicValue} className="input-field-text" type="text" name="topic" placeholder="Topic of entery"/>
                {topicError && <p className="input-error-text">Topic is required</p>}
            </div>
            <div className="input-field input-flex">
                <label>Diary Entery</label>
                <textarea onChange={enteryChangeHandler} onBlur={enteryBlurHandler} value={enteryValue} className="input-field-textarea" name="entery" placeholder="write a message here">
                </textarea>
                {enteryError && <p className="input-error-text">Please add a diary entery</p>}
            </div>
            <button  className="btn">Add Post</button>
        </form>
    )
}
export default DiaryInput
