export const addEntry = (data, token) => async (dispatch) => {
    const sendData = async () => {
        try{
            const response = await fetch("http://localhost:8080/api/entry/create", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": `bearer ${token}`
                },
                body: JSON.stringify(data)
            })
            if(!response){
                throw new Error("something went wrong")
            } 
            const resData = await response.json()
            console.log(resData)
             if(!resData.auth) {
                return 
            } 
            return resData
        }
        catch(e) {
            console.log(e)
        }

    }
    try{
        const entry = await sendData()
        if(!entry) {
            throw new Error("something went wrong")
        }
        else{
            dispatch({type:"ADD_ENTRY", payload: entry})
        }


    }
    catch(e) {
        console.log(e)
    }
}

export const deleteEntry = (id) => async (dispatch) => {
    const sendData = async () => {
        const response = await fetch(`http://localhost:8080/api/entry/delete/${id}`, {
