const reducer = (state = [], action) => {
    console.log("in reducer")
    switch(action.type) {
        case "ADD_ENTRY":
           return  [...state, action.payload]

        case "LOAD_ITEMS":
            console.log("in load items")
            return action.payload
        default:
            return state

    }   
}
export default reducer
