 export const getEntries = () =>   async (dispatch) => {
      const getData = async () => {
        try{
            const response = await fetch("http://localhost:8080/api/entry/fetch");
            if(!response){
                throw new Error("something went wrong")
            } 
            const data = await response.json() 
            return data.entries
        }
        catch(e){
            console.log(e)
        }
      }
    try{
        const entriesData = await getData()
        dispatch({type: "LOAD_ITEMS", payload: entriesData})

    }
    catch(e) {
        console.log(e)
        // add an error dispatch
    }


}

export const addEntry = (data) => async (dispatch) => {
    const sendData = async () => {
        console.log(data)
        try{
            const response = await fetch("http://localhost:8080/api/entry/create", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify(data)
            })
            if(!response){
                throw new Error("something went wrong")
            } 
            const resData = await response.json()
            return resData
        }
        catch(e) {
            console.log(e)
        }

    }
    try{
        const entry = await sendData()
        dispatch({type:"ADD_ENTRY", payload: entry})
    }
    catch(e) {
        console.log(e)
    }
}
