    {
        id: 3,
        title: "This is an entry 3",
        topic: "This is a topic",
        entry: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Morbi sollicitudin massa eu velit aliquet tincidunt. Nulla vitae euismod ligula. Vivamus sit amet sem id erat eleifend ultricies. Aenean vehicula sodales nibh a bibendum. Nam quis mauris sollicitudin, finibus velit eu, sodales elit. Nullam a ex luctus, consequat dui sit amet, condimentum augue. Mauris viverra, enim sed tempor elementum, elit nisi accumsan mi, at lobortis metus leo eget lorem. Nullam ac felis a justo rutrum aliquet vestibulum et tellus. Nullam ac lacus posuere, mollis urna sed, pulvinar velit. Interdum et malesuada fames ac ante ipsum primis in faucibus. Aliquam pretium elit quis urna blandit varius. Sed tempus sem id diam cursus, pellentesque euismod nibh vehicula"
    },
    {
        id: 4,
        title: "This is an entry 4",
        topic: "This is a topic",
        entry: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Morbi sollicitudin massa eu velit aliquet tincidunt. Nulla vitae euismod ligula. Vivamus sit amet sem id erat eleifend ultricies. Aenean vehicula sodales nibh a bibendum. Nam quis mauris sollicitudin, finibus velit eu, sodales elit. Nullam a ex luctus, consequat dui sit amet, condimentum augue. Mauris viverra, enim sed tempor elementum, elit nisi accumsan mi, at lobortis metus leo eget lorem. Nullam ac felis a justo rutrum aliquet vestibulum et tellus. Nullam ac lacus posuere, mollis urna sed, pulvinar velit. Interdum et malesuada fames ac ante ipsum primis in faucibus. Aliquam pretium elit quis urna blandit varius. Sed tempus sem id diam cursus, pellentesque euismod nibh vehicula"
    },
    {
        id: 5,
        title: "This is an entry 4",
        topic: "This is a topic",
        entry: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. "
    }


]
const classes = ["stories-entry1", "stories-entry-large", "stories-entry-small1", "stories-entry-small2", "stories-entry2" ]
const Stories = () => {
    let timeout;
    const token = useSelector(state => state.auth.token)
    const [wordLimit, setWordLimit] = useState(0)
    const [storiesState, setStoriesState] = useState([])
    const rangeOnChangeHandler = async (e) => {
        setWordLimit(e.target.value)
        timeout && clearTimeout(timeout)
        timeout = setTimeout( async () => {
            console.log("running")
            const query = `?words[lte]=${e.target.value}`
            try{
                const response = await fetch(`http://localhost:8080/api/entry/fetch${query}`, {
                    headers:{
                        "Authorization": `bearer ${token}`
                    }
                })
                const data = await response.json()
                setStoriesState(data)
            }
            catch(e) {
                console.log(e)
            }

        }, 5000)


    }
  const storyList =  stories.map((story, index) => {
       return <StoryItems classes={classes[index]} title={story.title} topic={story.topic} entry={story.entry} key={story.id}/>
    })

    return (
       <section className="stories-grid">
         <Controls wordLimit={wordLimit} onChangeHandler={rangeOnChangeHandler}/>
        {storyList}
         <section className="stories-pageCount"><h1>Page count here</h1></section>
       </section>
    )
}

export default Stories
