import { act } from "react-dom/test-utils";


 export const reducer = (state = [], action) => {
    switch(action.type) {
        case "ADD_ENTRY":
           return  state.concat(action.payload)
            break;
        case "Load_ENTRIES":
            return action.payload

    }


}
